mapper.select:
SELECT COUNT(*) from t_green_houses


        AND name like concat('%', #{condition.keyword}, '%')


        AND status = #{condition.status}


        AND zone_id = #{condition.zoneID}

SELECT h.*,z.name as zoneName FROM t_green_houses h LEFT JOIN t_zone z ON h.ZONE_ID=z.ID
AND name like concat('%', #{condition.keyword}, '%')
AND h.status = #{condition.status}
ORDER BY create_time desc LIMIT #{limit},#{size}
