mapper.select:
SELECT COUNT(*) from t_pest_monitor


        AND name like concat('%', #{condition.keyword}, '%')


        AND status = #{condition.status}

SELECT p.*,g.name as greenHouseName,pr.name as productName FROM t_pest_monitor p INNER JOIN t_green_houses g ON p.green_house_id = g.id INNER JOIN t_product pr ON p.product_id = pr.id
AND p.name like concat('%', #{condition.keyword}, '%')
AND p.status = #{condition.status}
ORDER BY create_time desc LIMIT #{limit},#{size}
